INSERT INTO `tbl_products` VALUES (6, 1, 'ƯU Đãi & Thanh Toán', 'ƯU Đãi & Thanh Toán', '1527305543_77957_thanh-toan-dien-tu-se-giup-dat-nuoc-phat-trien-nhanh-hon-1.jpg', '', '<div class=\"main-content\">\n<div id=\"accordion\" class=\"career-list panel-group\">\n<div id=\"divContent\" class=\"editor-content\">\n<div class=\"content\">\n<div id=\"utilities\">\n<p style=\"text-align: center;\"><span style=\"color: #008000;\"><strong><span style=\"font-size: x-large;\">CHÍNH SÁCH ƯU ĐÃI:</span></strong></span></p>\n<p style=\"text-align: center;\"><span style=\"color: #008000;\"><strong><span style=\"font-size: x-large;\"> • Rút thăm trúng thưởng giải đặc biệt trị giá <span style=\"color: #ff0000;\">500 triệu đồng. </span></span></strong></span></p>\n<p style=\"text-align: center;\"><span style=\"color: #008000;\"><strong><span style=\"font-size: x-large;\">• Rút thăm trúng thưởng giải 1 – 2 – 3 và giải khuyến khích vào ngày tổ chức sự kiện khai trương nhà mẫu. </span></strong></span></p>\n<p style=\"text-align: center;\"><span style=\"color: #008000;\"><strong><span style=\"color: #008000;\">Phần thưởng bao gồm : Xe SH và Tivi 32 inh</span></strong></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: x-large; color: #008000;\">Thông tin bảng giá và thanh toán chính xác nhất Quý khách vui lòng liên hệ</span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: x-large; color: #008000;\"><strong>Hotline: <span style=\"color: #ff0000;\"><strong>0909.338.234</strong></span></strong></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: x-large; color: #008000;\"><strong><span style=\"font-size: x-large;\">Hoặc để lại thông tin tại mục Liên Hệ.</span></strong></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: x-large; color: #008000;\"><strong><span style=\"font-size: x-large;\">Chuyên viên kinh doanh sẽ liên hệ quý khách trong vài phút</span></strong></span></p>\n</div>\n</div>\n</div>\n</div>\n</div>', 1, '2018-05-30 08:03:48', NULL, NULL);
INSERT INTO `tbl_products` VALUES (7, 9, 'Tổng Quan Dự Án', 'Tổng Quan Dự Án', '1553275647_64397_16649293_1125719950906703_3382876523231788012_n.jpg', 'Mô tả dfsdkjsdjsdvk sfksfksdsdfs', '<p><img title=\"20180505125230-95a3\" src=\"https://hoanglongriverside.vn/upload/images/images/2018/05/26/1527305484_20180505125230-95a3_wm.jpg\" alt=\"\" width=\"\" height=\"\" border=\"0\" /><img title=\"72c5ba8a5106bf58e617.jpg\" src=\"https://hoanglongriverside.vn/upload/images/images/2018/05/26/1527305509_72c5ba8a5106bf58e617.jpg\" alt=\"\" width=\"\" height=\"\" border=\"0\" /></p>', 1, '2019-03-22 17:27:44', NULL, NULL);
INSERT INTO `tbl_products` VALUES (8, 2, 'Khu căn hộ Green Star Sky Garden chính thức ra mắt thị trường', 'Khu căn hộ Green Star Sky Garden chính thức ra mắt thị trường', '1507882144_86096_can-ho-the-goldenstar-phong-khach.jpg', 'Chủ đầu tư Hưng Lộc Phát chính thức công bố thông tin dự án căn hộ Green Star Sky Garden. Dự án Green Star Sky Garden nằm trong tổng thể dự án Hưng Phát Green Star. Quy mô hơn 100 căn biệt thự phố và hơn 1000 căn hộ tầm trung. ...', '<h3 data-fontsize=\"14\" data-lineheight=\"21\"><em><strong>Ngày 13-3-2016, Công ty <a title=\"Hưng Lộc Phát\" href=\"http://www.hunglocphat.vn/\">Hưng Lộc Phát</a> đã tổ chức lễ kỷ niệm 15 năm thành lập và chính thức công bố dự án khu căn hộ cao cấp The Golden Star (72 Nguyễn Thị Thập, Quận 7, TP.HCM) thuộc quy hoạch khu dân cư cao cấp Nam sài Gòn với sự tham gia của hơn 900 khách hàng.</strong></em></h3>\r\n<p><img class=\"size-full wp-image-1914 aligncenter\" src=\"http://www.hunglocphat.com/wp-content/uploads/2016/09/le-ky-niem-hlp-15-nam-20.jpg\" alt=\"le-ky-niem-hlp-15-nam-20\" width=\"2657\" height=\"1772\" /></p>\r\n<p>Hơn 900 trăm khách hàng tham gia sự kiện của Hưng Lộc Phát tại khán phòng Trung tâm hội nghị Gem Center</p>\r\n<h3 data-fontsize=\"14\" data-lineheight=\"21\"><strong>Khi dự án bắt đúng mạch thị trường</strong></h3>\r\n<p>Tọa lạc tại vị trí đắc địa ngay tại trung tâm quận 7, The Golden Star là dự án khu căn hộ cao cấp được quy hoạch hiện đại với tổng diện tích 9.229m2 , được xây dựng với quy mô 26 tầng, tổng cộng 478 căn hộ, diện tích từ 50.75-83.13 m2 ( đến 3 phòng ngủ).Đây là căn hộ thuộc dòng sản phẩm cao cấp của chủ đầu tư Hưng Lộc Phát, thương hiệu sản xuất vật liệu xây dựng, đá ốp lát cao cấp uy tín lâu năm, The Golden Star được lát gạch Cẩm Thạch, ván lót sàn nhập khẩu từ Đức. Ngoài ra, để đảm bảo an toàn tuyệt đối cho cư dân, dự án được trang bị hệ thống bảo vệ an ninh 3 lớp, quản lý bằng video call và thẻ từ.</p>\r\n<p><img class=\"aligncenter size-full wp-image-1942\" src=\"http://www.hunglocphat.com/wp-content/uploads/2016/09/le-ky-niem-15-nam-du-an-the-golden-star-30.jpg\" alt=\"le-ky-niem-15-nam-du-an-the-golden-star-30\" width=\"2657\" height=\"1772\" /></p>\r\n<p>Rất đông khách hàng đến tìm hiểu thông tin về dự án The Golden Star của Công ty Hưng Lộc Phát</p>\r\n<p>    Trong đợt công bố này, Hưng Lộc Phát đưa ra mức giá rất hấp dẫn chỉ từ 25.3 triệu đồng/m2 . Được biết, mức giá công bố này thấp hơn các dự án trong khu vực khoảng 3 triệu/m2. Hoa hậu Lệ Quyên- người đoạt giải hoa hậu được yêu thích nhất trên mạng xã hội trong cuộc thi hoa hậu siêu quốc gia năm 2015 được tổ chức tại Ba Lan cũng chính là một trong những khách hàng đầu tiên sở hữu căn hộ cao cấp the Golden Star. Sau khi ký hợp đồng mua bán hoa hậu Lệ Quyên cho biết từ lâu đã muốn tìm kiếm một không gian sống đầy đủ tiện ích khép kín, gần gũi thiên nhiên và an ninh được đảm bảo tối đa vì phải thường thường xuyên bận rộn với các chuyến công tác dài ngày. Hoa hậu cũng đã tham quan nhiều dự án khác trước khi quyết định “chọn mặt gửi vàng” cho The Golden Star. “The Golden Star được xây dựng theo mô hình khu dân cư khép kín và dành phần lớn diện tích cho mảng xanh, khu sinh hoạt cộng đồng 5 sao, hồ bơi… Ngoài ra, không gian nơi đây rất yên tĩnh, phù hợp cho nhu cầu nghỉ dưỡng sau những giờ làm việc căng thẳng của tôi”, Hoa hậu nói và cho biết thêm rằng, bên cạnh các tiện ích nội khu,dự án cũng có lợi thế lớn khi được thụ hưởng toàn bộ tiện ích của khu đô thị Phú Mỹ Hưng nằm liền kề nhưng giá lại tốt hơn rất nhiều và tránh được sự ồn ào, đông đúc.</p>\r\n<p><img class=\"aligncenter size-full wp-image-1906\" src=\"http://www.hunglocphat.com/wp-content/uploads/2016/09/le-ky-niem-hlp-15-nam-13.jpg\" alt=\"le-ky-niem-hlp-15-nam-13\" width=\"2657\" height=\"1772\" /></p>\r\n<p>    Hoa hậu Lệ Quyên</p>\r\n<p>    Tương tự hoa hậu Lệ Quyên, rất nhiều khách hàng khác đều cho biết bên cạnh môi trường sống tuyệt vời, họ còn nhận thấy dự án có nhiều tiềm năng gia tăng giá trị rất lớn trong tương lai không xa. Khách hàng Nguyễn Anh Tuấn chia sẻ: “The Golden Star được đầu tư bài bản, môi trường sống tiện nghi, tốt cho sức khỏe nhưng tính ra giá bán chỉ tương đương, thậm chí tốt hơn các căn hộ trong khu vực. Vài người bạn của tôi cũng rất thích và đang chờ Hưng Lộc Phát mở bán đợt 2 để mua”.</p>\r\n<h3 data-fontsize=\"14\" data-lineheight=\"21\"><strong>Tiện ích vượt trội, địa thế thịnh vượng</strong></h3>\r\n<p>Nhằm kiến tạo cho cộng đồng một không gian sống tiện nghi, hiện đại và gần gũi với môi trường thiên nhiên như một thành phố xanh thu nhỏ, The Golden Star dành phần lớn diện tích cho những mảng xanh và các tiện ích nội khu. Cụ thể mật độ xây dựng chỉ chiếm 32,24%, dự án được thiết kế biệt lập, khép kín với tường bao có vườn treo kết hợp cây xanh tạo cảnh quan thiên nhiên thân thiện.</p>\r\n<p>Bên cạnh đó, dự án còn sở hữu hàng loạt những tiện ích đẳng cấp khác như: Hồ bơi Sky View tràn bờ diện tích trên 500 m2 tại tầng 21, điều chỉnh nhiệt độ theo thời tiết, phòng Gym, khu BBQ, sân vườn, siêu thị Vinmart, nhà sinh hoạt cộng đồng 5 sao… Đặc biệt, The Golden Star là dự án đầu tiên tại TP.HCM được đầu tư hệ thống xử lý nước công nghệ cao, nước lọc tại vòi đạt tiêu chuẩn nước uống trực tiếp của Mỹ. Hệ thống thang máy cao cấp hàng đầu thế giới OTIS với tốc độ cao cũng sẽ giúp cư dân thêm thuận tiện khi di chuyển.<br /><img class=\"aligncenter size-full wp-image-1948\" src=\"http://www.hunglocphat.com/wp-content/uploads/2016/09/2-0-PHOI-CANH-HO-BOI-THE-GOLDEN-STAR.jpg\" alt=\"2--0-PHOI-CANH-HO-BOI-THE-GOLDEN-STAR\" width=\"4961\" height=\"3264\" /></p>\r\n<p>Phối cảnh hồ bơi Sky View tràn bờ</p>\r\n<h3 data-fontsize=\"14\" data-lineheight=\"21\"><strong>“Chọn mặt gửi vàng” chủ đầu tư uy tín </strong></h3>\r\n<p>The Golden Star là dự án mới nhất của Hưng Lộc Phát  sau khi thành công các dự án cao ốc Hưng Phát, <a title=\"Hưng Phát Silver Star\" href=\"http://hunglocphat.com/hung-phat-silver-star.html\">Hưng Phát Silver Star</a>……Ông Nguyễn Dư Lực, Chủ tịch HĐQT Hưng Lộc Phát, cho biết công ty đã dành rất nhiều tâm huyết triển khai dự án với mong muốn đem đến cho khách hàng một không gian sống hiện đại, tiện nghi và môi trường sống nhận văn với chi phí hợp lý nhất. “Để đảm bảo cho một không gian sống đẳng cấp và chất lượng, chúng tôi phải tuyển chọn thương hiệu tư vấn hàng đầu đảo quốc Singapore là Ong &amp; Ong (đơn vị tư vấn thiết kế cho dự án Vinhomes Tân Cảng) . Ngay từ đầu, mọi công đoạn từ thiết kế quy hoạch, thiết kế kiến trúc và thiết kế kỹ thuật đều do Ong &amp; Ong đảm nhiệm nhằm đảm bảo sự hoàn hảo ở mức cao nhất”, ông Lực cho biết:</p>\r\n<p>Trong khuôn khổ chương trình kỷ niệm 15 năm thành lập, Công ty Hưng Lộc còn tiến hành bàn giao giấy chứng nhận quyền sở hữu nhà ở cho các cư dân dự án Hưng Phát1. Được biết trước đó,ở dự án này Hưng Lộc Phát đã nâng cấp một số hạng mục có chất lượng và tính thẩm mỹ cao hơn cho khách hàng so với những cam kết ban đầu trong hợp đồng. Cụ thể, với thiết kế ban đầu của mặt dựng cầu thang máy là gạch Ceramic nhưng Hưng Lộc Phát đã cho thay bằng đá Marble, là loại đá tự nhiên cao cấp mà chỉ lắp đặt tại khách sạn tầm cỡ 5 sao trở lên, hoặc hành lang thay vì là gạch Ceramic thì Hưng Lộc Phát đã lát đá Granite vừa mang tính thẩm mỹ vừa tăng tuổi thọ sử dụng của tòa nhà …Điều này mang lại sự hài lòng ngoài mong đợi của các khách hàng đã chọn mua dự án Hưng Phát 1 nên chỉ sau 3 tháng bàn giao đã được lấp đầy 100% cư dân đến sinh sống. Điều này sẽ là một minh chứng rõ ràng cho nội lực vững mạnh và uy tín cùng cam kết vàng “nói được làm được” của Hưng Lộc Phát. Đó là kết quả của chặng hành trình hơn 15 năm nỗ lực, củng cố sức mạnh nội tại doanh nghiệp, tăng cường năng lực tài chính với mục tiêu đem lại những giá trị gia tăng bền vững cho khách hàng và xã hội.</p>\r\n<h3 data-fontsize=\"14\" data-lineheight=\"21\"><strong>Một số hình ảnh tại chương trình hội nghị:</strong></h3>\r\n<p><img class=\"aligncenter size-full wp-image-1950\" src=\"http://www.hunglocphat.com/wp-content/uploads/2016/09/le-ky-niem-15-nam-du-an-the-golden-star-34.jpg\" alt=\"le-ky-niem-15-nam-du-an-the-golden-star-34\" width=\"2657\" height=\"1772\" /></p>\r\n<p>Ông Nguyễn Dư Lực, Chủ tịch HĐQT Cty Hưng Lộc Phát phát biểu tại chương trình</p>\r\n<p><img class=\"aligncenter size-full wp-image-1913\" src=\"http://www.hunglocphat.com/wp-content/uploads/2016/09/le-ky-niem-hlp-15-nam-19.jpg\" alt=\"le-ky-niem-hlp-15-nam-19\" width=\"2657\" height=\"1772\" /></p>\r\n<p>Ông David Ching – GĐ Cty Ong &amp;Ong – Singapore phát biểu tại buổi lễ</p>\r\n<p><img class=\"aligncenter size-full wp-image-1916\" src=\"http://www.hunglocphat.com/wp-content/uploads/2016/09/le-ky-niem-hlp-15-nam-22.jpg\" alt=\"le-ky-niem-hlp-15-nam-22\" width=\"2657\" height=\"1772\" /></p>\r\n<p>Ông Lê Văn Tòng – GĐ NH Vietinbank chi nhánh 5 TPHCM phát biểu tại buổi lễ</p>\r\n<p><img class=\"aligncenter size-full wp-image-1952\" src=\"http://www.hunglocphat.com/wp-content/uploads/2016/09/le-ky-niem-15-nam-du-an-the-golden-star-31.jpg\" alt=\"le-ky-niem-15-nam-du-an-the-golden-star-31\" width=\"2657\" height=\"1772\" /></p>\r\n<p>Ông Lê Hoàng Châu – Chủ tịch Hiệp hội <a title=\"Bất động sản\" href=\"http://hunglocphat.com/bat-dong-san\">Bất động sản</a> TP.HCM tặng hoa chúc mừng cho ông Nguyễn Dư Lực – Chủ tịch HĐQT Cty Hưng Lộc Phát</p>\r\n<p><img class=\"aligncenter size-full wp-image-1959\" src=\"http://www.hunglocphat.com/wp-content/uploads/2016/09/le-ky-niem-15-nam-du-an-the-golden-star-32.jpg\" alt=\"le-ky-niem-15-nam-du-an-the-golden-star-32\" width=\"2657\" height=\"1772\" /></p>\r\n<p>Từ trái sang: ông Nguyễn Thanh Tịnh – PCT UBND tỉnh Bà Rịa Vũng Tàu, ông Nguyễn Văn Thiệp – Bí thư huyện Ủy Cần Giuộc, ông Phạm Lê Tuấn – TGĐ Cty, ông Nguyễn Dư Lực – Chủ tịch HĐQT Cty, ông Trần Văn Mười – CT HĐQT Tập đoàn quốc tế 5 sao khui champagne chúc mừng kỷ niệm 15 năm thành lập Cty Hưng Lộc Phát</p>\r\n<p><img class=\"aligncenter size-full wp-image-1960\" src=\"http://www.hunglocphat.com/wp-content/uploads/2016/09/le-ky-niem-15-nam-du-an-the-golden-star-24.jpg\" alt=\"le-ky-niem-15-nam-du-an-the-golden-star-24\" width=\"2657\" height=\"1772\" /></p>\r\n<p>Ông Phạm Lê Tuấn, TGĐ Công ty Hưng Lộc Phát (đầu tiên từ phải qua) trao giấy chứng nhận quyền sử dụng nhà ở cho cư dân dự án Cao ốc Hưng Phát</p>\r\n<p><img class=\"aligncenter size-full wp-image-1961\" src=\"http://www.hunglocphat.com/wp-content/uploads/2016/09/le-ky-niem-15-nam-du-an-the-golden-star-25.jpg\" alt=\"le-ky-niem-15-nam-du-an-the-golden-star-25\" width=\"2657\" height=\"1772\" /></p>\r\n<p>Hoa hậu Lệ Quyên (đứng giữa) là một trong những cư dân đầu tiên của dự án The Golden Star chia sẻ lý do quyết định chọn dự án này</p>\r\n<p><img class=\"aligncenter size-full wp-image-1962\" src=\"http://www.hunglocphat.com/wp-content/uploads/2016/09/le-ky-niem-15-nam-du-an-the-golden-star-26.jpg\" alt=\"le-ky-niem-15-nam-du-an-the-golden-star-26\" width=\"2657\" height=\"1772\" /></p>\r\n<p>Ông Trương Văn Tích, PTGĐ Công ty Hưng Lộc Phát trao thưởng giải ba cho khách hàng tham gia chương trình rút thăm trúng thưởng tại sự kiện</p>\r\n<p><img class=\"aligncenter size-full wp-image-1963\" src=\"http://www.hunglocphat.com/wp-content/uploads/2016/09/le-ky-niem-15-nam-du-an-the-golden-star-27.jpg\" alt=\"le-ky-niem-15-nam-du-an-the-golden-star-27\" width=\"2657\" height=\"1772\" /></p>\r\n<p>Ông Phạm Lê Tuấn, TGĐ Công ty Hưng Lộc Phát trao thưởng giải nhì cho khách hàng tham gia chương trình rút thăm trúng thưởng tại sự kiện.</p>\r\n<p><img class=\"aligncenter size-full wp-image-1965\" src=\"http://www.hunglocphat.com/wp-content/uploads/2016/09/le-ky-niem-15-nam-du-an-the-golden-star-28.jpg\" alt=\"le-ky-niem-15-nam-du-an-the-golden-star-28\" width=\"2657\" height=\"1772\" /></p>\r\n<p>Ông Nguyễn Dư Lực, Chủ tịch HĐQT Công ty Hưng Lộc Phát trao thưởng giải đặc biệt cho khách hàng tham gia chương trình rút thăm trúng thưởng tại sự kiện</p>\r\n<h3 data-fontsize=\"14\" data-lineheight=\"21\">Video sự kiện Lễ kỷ niệm 15 năm thành lập Hưng Lộc Phát và Công bố dự án The Golden Star:</h3>\r\n<div class=\"fusion-fullwidth fullwidth-box fusion-blend-mode hundred-percent-fullwidth\">\r\n<div class=\"fusion-builder-row fusion-row \">\r\n<div class=\"fusion-layout-column fusion_builder_column fusion_builder_column_1_1  fusion-one-full fusion-column-first fusion-column-last fusion-column-no-min-height 1_1\">\r\n<div class=\"fusion-column-wrapper\" data-bg-url=\"\"><iframe src=\"https://www.youtube.com/embed/LHjHB3oHPhw?feature=oembed\" frameborder=\"0\" width=\"1170\" height=\"658\"></iframe></div>\r\n</div>\r\n</div>\r\n</div>', 2, '2018-04-03 09:23:48', NULL, '1');
INSERT INTO `tbl_products` VALUES (21, 2, '[Tuổi Trẻ] Căn hộ Hưng Lộc Phát: Sản phẩm thật, nhu cầu thật', '[Tuổi Trẻ] Căn hộ Hưng Lộc Phát: Sản phẩm thật, nhu cầu thật', '1506153236_99744_3.jpg', 'Không phải là một thương hiệu nổi tiếng trong giới kinh doanh bất động sản, nhưng Hưng Lộc Phát là một trong số ít doanh nghiệp kinh doanh bất động sản tại Thành phố Hồ Chí Minh đã mang lại sự hài lòng trọn vẹn của khách hàng mua và sinh sống tại Cao ốc Hưng Phát.', '<h3 data-fontsize=\"14\" data-lineheight=\"21\"><strong><em>Không phải là một thương hiệu nổi tiếng trong giới kinh doanh bất động sản, nhưng <a title=\"Hưng Lộc Phát\" href=\"http://www.hunglocphat.vn/\">Hưng Lộc Phát</a> là một trong số ít doanh nghiệp kinh doanh bất động sản tại Thành phố Hồ Chí Minh đã mang lại sự hài lòng trọn vẹn của khách hàng mua và sinh sống tại Cao ốc Hưng Phát. Đó là nhờ sự tận tâm, thấu hiểu và nỗ lực tối đa của chủ đầu tư đối với khách hàng và luôn luôn đặt nhu cầu của khách vào vị trí của mình nhằm mang lại sự hoàn hảo nhất cho từng chi tiết từ khâu thiết kế đến từng biện pháp thi công trong quá trình xây dựng.</em></strong></h3>\n<h3 data-fontsize=\"14\" data-lineheight=\"21\"><strong>Uy tín trong giao kết- chất lượng trong sản phẩm</strong></h3>\n<p>Sẽ không có gì thật sự ấn tượng nếu những con số thể hiện trên báo cáo kinh doanh là kết quả của một doanh nghiệp hoạt động trong một điều kiện kinh tế thuận lợi. Nhưng thực tế, năm 2013 nền kinh tế Việt Nam nói chung và lĩnh vực bất động sản nói riêng đang đối diện với hàng loạt những khó khăn. Trong khi nhiều doanh nghiệp phải nỗ lực để bám trụ thì Hưng Lộc Phát lại đạt được những kết quả đáng ghi nhận. Dù là dự án đầu tay nhưng chỉ sau 2 tuần đã bán gần 200 căn hộ Cao ốc Hưng Phát 1 và bàn giao nhà cho khách hàng đúng tiến độ như đã cam kết trong hợp đồng trong bối cảnh thị trường bất động sản tại thời điểm đó còn rất khó khăn. Để làm được điều đó là cả một quá trình nỗ lực của ban lãnh đạo công ty để tạo được sức mạnh về uy tín thương hiệu trong lòng khách hàng cũng như đối tác.</p>\n<p>“Với sứ mệnh mang đến những sản phẩm chất lượng cao và uy tín trong từng giao kết với khách hàng, chúng tôi đã không ngừng mở rộng mạng lưới hoạt động, tăng cường hợp tác đầu tư với các đối tác lớn, thu hút đầu tư để nâng cao năng lực cạnh tranh”, ông Nguyễn Dư Lực, Chủ tịch Hội đồng quản trị Công ty Hưng Lộc Phát nói và chia sẻ thêm rằng công ty sẽ tiếp tục mang đến sự hài lòng cho quý khách hàng bằng những sản phẩm chất lương vượt trội và chuẩn mực trong tất cả những cam kết.</p>\n<h3 data-fontsize=\"14\" data-lineheight=\"21\"><strong>Chất lượng công trình vượt mong đợi của khách hàng</strong></h3>\n<p>Với thế mạnh là doanh nghiệp sản xuất vật liệu xây dựng , Công ty cổ phần Đầu tư Bất động sản Hưng Lộc Phát đã nâng cấp một số hạng mục có chất lượng và tính thẩm mỹ cao hơn cho khách hàng so với những cam kết ban đầu trong hợp đồng. Cụ thể, với thiết kế ban đầu của mặt dựng cầu thang máy là gạch Ceramic nhưng Hưng Lộc Phát đã cho thay bằng đá Marble, là loại đá tự nhiên cao cấp mà chỉ lắp đặt tại khách sạn tầm cỡ 5 sao trở lên, hoặc hành lang thay vì là gạch Ceramic thì Hưng Lộc Phát đã lát đá Granite vừa mang tính thẩm mỹ vừa tăng tuổi thọ sử dụng của tòa nhà … Điều này mang lại sự hài lòng ngoài mong đợi của các khách hàng đã chọn mua dự án Hưng Phát 1 nên chỉ sau 3 tháng bàn giao đã được lấp đầy 100% cư dân đến sinh sống.</p>\n<p><img class=\"aligncenter wp-image-2015\" src=\"http://www.hunglocphat.com/wp-content/uploads/2016/09/phoi-canh-buoi-toi-the-golden-star.jpg\" alt=\"phoi-canh-buoi-toi-the-golden-star\" width=\"680\" height=\"446\" /></p>\n<p>Dịch vụ quản lý chuyên nghiệp với chi phí thấp</p>\n<p>    Chủ đầu tư, Công ty Hưng Lộc Phát luôn mong muốn cư dân sống trong hệ thống các dự án của công ty được hưởng mức phí các dịch vụ ở mức thấp nhất nhưng các tiện ích đầy đủ và hoàn thiện nhất. Hiện tai cao ốc Hưng Phát 1, chủ đầu tư đã giao toàn bộ kinh doanh phần tầng hầm để xe cho Ban quản trị chung cư điều hành, toàn bộ doanh thu được Ban quản trị thu bù đắp thêm vào chi phí vận hành nhằm giảm phí quản lý cho cư dân tại đây. Nếu so với mặt bằng chung tại các dự án chung cư khác thì phí quản lý của cao ốc Hưng Phát hiện đang thấp hơn khoảng 50%.</p>\n<h3 data-fontsize=\"14\" data-lineheight=\"21\"><strong>Nhận sổ hồng ngay sau 1 năm bàn giao căn hộ</strong></h3>\n<p>Nhiều dự án tại Tp. HCM bàn giao căn hộ đã nhiều năm nhưng chủ đầu tư không thể làm các thủ tục pháp lý để cấp sổ đỏ cho các căn hộ vì trong quá trình xây dựng chủ đầu tư đã thế chấp sổ đỏ của dự án cho ngân hàng để triển khai dự án khác , hoặc chưa hoàn thành nghĩa vụ tài chính đóng tiền chuyển mục đích sử dụng đất cho cơ quan nhà nước, hoặc bị thiếu một số thủ tục pháp lý khác không đủ điều kiện cấp sổ đỏ cho khách hàng. Riêng với dự án Hưng Phát 1 của Công ty Hưng Lộc Phát có thể nói là dự án hiếm hoi đã hoàn thành mọi thủ tục pháp lý nộp hồ sơ tại phòng đăng ký quyền sử dụng đất của thành phố và dự kiến sẽ giao sổ đỏ sớm hơn tiến độ cam kết với khách hàng.</p>\n<h3 data-fontsize=\"14\" data-lineheight=\"21\"><strong>Chia sẻ lợi nhuận với khách hàng bằng giá bán hợp lý.</strong></h3>\n<p>Giá cả hợp lý là 1 trong 3 yếu tố quyết định đến thành công của một dự án . Muốn giá căn hộ thấp hơn so với mặt bằng khu vực thì có rất nhiều cách. Với Hưng Lộc Phát, đã phát huy thế mạnh riêng có để giảm giá thành. Nhờ vào nguồn nguyên vật liệu xây dựng do chính mình sản xuất như đá Granite, Marble cùng bê tông tươi… và việc hoạch định theo chiến lượt phát triển dài hạn nên đã chuẩn bị sẵn một quỹ đất từ rất lâu nên giá thành sản phẩm khá thấp so với các sản phẩm cùng phân khúc. Với yếu tố giá thành hợp lý như vậy nên các dự án của Hưng Lộc Phát luôn tạo ra giá trị gia tăng rất lớn cho khách hàng. Cụ thể là các khách hàng đã mua dự án Hưng Phát 1 hiện đã có mức chênh lệch lên đến 4-5 triệu/m² chỉ sau sau 1 năm bàn giao và dự án Silver Star có giá trị gia tăng từ 2-3 triệu/m², sau 6 tháng kể từ ngày mở bán.</p>\n<p><img class=\"aligncenter size-full wp-image-2102\" src=\"http://www.hunglocphat.com/wp-content/uploads/2016/09/su-khac-biet-cua-can-ho-5-sao-the-golden-star-4.jpg\" alt=\"su-khac-biet-cua-can-ho-5-sao-the-golden-star-4\" width=\"600\" height=\"312\" /></p>\n<p>Tiếp nối thành công đó, Hưng Lộc Phát đã chính thức tung ra thị trường dự án The Golden Star vào giữa quý I-2016. Đây là dự án hội tụ đầu đủ những tiện ích đẳng cấp hàng đầu trong khu vực Quận 7. Theo thông tin từ chủ đầu tư, vào ngày 13-3 sắp tới đây, cũng là ngày kỷ niệm 15 năm thành lập Công ty Hưng Lộc Phát, sẽ tổ chức bàn giao sổ hồng đợt 1 cho cư dân mua căn hộ Hưng Phát 1 và tri ân các khách hàng đã ủng hộ cho dự án <a title=\"Hưng Phát Silver Star\" href=\"http://hunglocphat.com/hung-phat-silver-star.html\">Hưng Phát Silver Star</a>. Cũng tại buổi lễ này Công ty Hưng Lộc Phát chính thức giới thiệu ra thị trường dự án đẳng cấp nhất mang thương hiệu The Golden Star tọa lạc tại số 72 Nguyễn Thị Thập, phường Bình Thuận, Quận 7 .</p>\n<p>Kế thừa kinh nghiệm của 2 dự án đầu tay, dự án The Golden Star được chủ đầu tư định vị đẳng cấp của dự án ngay từ các khâu lựa chọn đơn vị tư vấn thiết kế ban đầu . Điển hình, công ty đã chọn đơn vị tư vấn thiết kế là Công ty Ong &amp; Ong của Singapore, một trong những đơn vị có uy tín hàng đầu và đang thiết kế cho nhiều dự án lớn tại TPHCM, như dự án The Vinhomes Central Park Tân Cảng..,tư vấn quản lý và giám sát xây dựng là Tập đoàn Artelia (Pháp), nhà thầu thi công dự án nằm trong top 5 của khu vực Phía Nam.</p>\n<p>Dự án được quy hoạch khép kín, biệt lập, an ninh tuyệt đối 3 lớp bảo vệ. Đặc biệt, đây là dự án đầu tiên của TP. HCM có lắp đặt hệ thống xử lý, cung cấp nước uống tại vòi theo tiêu chuẩn Mỹ trong từng căn hộ. Hồ bơi tại tầng 21 được lắp đặt hệ thống điều chỉnh nhiệt độ theo thời tiết , cầu thang máy là của nhà cung cấp Otis (Một thương hiệu thang máy nổi tiếng của Mỹ), các thiết bị trong căn hộ được lựa chọn từ các hãng nổi tiếng đạt tiêu chuẩn 5 sao…</p>\n<p>Được biết dự án The Golden Star có mức giá hết sức hấp dẫn cho các nhà đầu tư cũng như các cư dân có có nhu cầu nhà ở thực sự, giá căn hộ chỉ từ 1,4 tỷ/ căn, với mức giá này căn hộ The Golden Star đang thấp hơn các dự án xung quanh khu vực khoảng 3 triệu/ m². Đây cũng là cơ hội cho tất cả các khách hàng đang tìm kiếm một không gian sống đẳng cấp và hiện đại bật nhất tại Nam Sài Gòn.</p>\n<p><strong>Linh Phi</strong></p>\n<h3 data-fontsize=\"14\" data-lineheight=\"21\"><strong>Hình ảnh bài viết được đăng tải trên báo Tuổi Trẻ: </strong></h3>\n<p><img class=\"aligncenter size-full wp-image-2162\" src=\"http://www.hunglocphat.com/wp-content/uploads/2016/09/Tuoi-Tre-14.03.2016.jpg\" alt=\"Tuoi-Tre-14.03.2016\" width=\"360\" height=\"480\" /></p>\n<p><cite><strong>Mọi thông tin liên hệ: </strong></cite><br /><cite><strong>Công ty Cổ phần Đầu tư <a title=\"Bất Động Sản\" href=\"http://hunglocphat.com/bat-dong-san.html\">Bất Động Sản</a> Hưng Lộc Phát </strong></cite><br /><a href=\"http://www.localhost/an/\"><cite><strong>http://www.localhost/an</strong></cite></a></p>', 2, '2017-09-23 14:53:56', NULL, NULL);
INSERT INTO `tbl_products` VALUES (22, 2, '[Thesaigontimes] Sự khác biệt của căn hộ 5 sao The Golden Star', '[Thesaigontimes] Sự khác biệt của căn hộ 5 sao The Golden Star', '1506153316_210635_2.jpg', ' Với việc toàn bộ 270 căn hộ được khách hàng đặt chỗ mua chỉ trong vài tuần công bố ra thị trường, khu căn hộ cao cấp The Golden Star đang tạo nên làn sóng mới cho thị trường bất động sản khu Nam Sài Gòn. ', '<h3 data-fontsize=\"14\" data-lineheight=\"21\"><strong><em> Với việc toàn bộ 270 căn hộ được khách hàng đặt chỗ mua chỉ trong vài tuần công bố ra thị trường, khu căn hộ cao cấp The Golden Star đang tạo nên làn sóng mới cho thị trường bất động sản khu Nam Sài Gòn. Vậy dự án được Công ty Hưng Lộc Phát đầu tư theo tiêu chuẩn 5 sao này có gì khác biệt?</em></strong></h3>\r\n<p><img class=\"aligncenter size-full wp-image-2099\" src=\"http://www.hunglocphat.com/wp-content/uploads/2016/09/su-khac-biet-cua-can-ho-5-sao-the-golden-star-1.jpg\" alt=\"su-khac-biet-cua-can-ho-5-sao-the-golden-star-1\" width=\"600\" height=\"401\" /></p>\r\n<p>Phòng khách, phòng ăn và bếp của căn hộ The Golden Star được thiết kế liên thông, tạo không gian thông thoáng và hiện đại cho không gian sống của chủ nhân. Trong căn hộ này, chủ nhân hoàn toàn an tâm về sức khỏe khi sử dụng hệ thống xử lý nước lọc tại vòi đạt tiêu chuẩn nước uống trực tiếp của Mỹ.</p>\r\n<p><img class=\"aligncenter size-full wp-image-2100\" src=\"http://www.hunglocphat.com/wp-content/uploads/2016/09/su-khac-biet-cua-can-ho-5-sao-the-golden-star-2.jpg\" alt=\"su-khac-biet-cua-can-ho-5-sao-the-golden-star-2\" width=\"600\" height=\"401\" /></p>\r\n<p>Toàn bộ toilet của căn hộ được lót gạch cẩm thạch sang trọng và sử dụng thiết bị vệ sinh, vòi nước cao cấp của Toto. Đáng chú ý, toàn bộ thiết bị này và hệ thống tủ bếp cao cấp, sàn gỗ nhập khẩu từ Đức… trị giá 340 triệu đồng đều được chủ đầu tư tặng kèm cho khách hàng mua căn hộ.</p>\r\n<p><img class=\"aligncenter size-full wp-image-2101\" src=\"http://www.hunglocphat.com/wp-content/uploads/2016/09/su-khac-biet-cua-can-ho-5-sao-the-golden-star-3.jpg\" alt=\"su-khac-biet-cua-can-ho-5-sao-the-golden-star-3\" width=\"600\" height=\"401\" /></p>\r\n<p>Phòng ngủ chính thiết kế ấm cúng và sang trọng với khung cửa sổ rộng cho tầm nhìn toàn cảnh thành phố từ trên cao. Giấc ngủ của chủ nhân càng trở nên êm ái, an lành khi mở cửa đón làn gió mát lạnh từ sông Sài Gòn thổi vào không dứt.</p>\r\n<p><img class=\"aligncenter size-full wp-image-2102\" src=\"http://www.hunglocphat.com/wp-content/uploads/2016/09/su-khac-biet-cua-can-ho-5-sao-the-golden-star-4.jpg\" alt=\"su-khac-biet-cua-can-ho-5-sao-the-golden-star-4\" width=\"600\" height=\"312\" /></p>\r\n<p>Hồ bơi sky view rộng 500 m2 nằm trên tầng 21 của The Golden Star tích hợp công nghệ tự động điều chỉnh nhiệt độ theo thời tiết.</p>\r\n<p>Đây là khu vực thư giãn tuyệt vời dành cho cư dân, vừa có thể rèn luyện sức khỏe vừa ngắm toàn cảnh thành phố từ mọi hướng.</p>\r\n<p><img class=\"aligncenter size-full wp-image-2104\" src=\"http://www.hunglocphat.com/wp-content/uploads/2016/09/su-khac-biet-cua-can-ho-5-sao-the-golden-star-5.jpg\" alt=\"su-khac-biet-cua-can-ho-5-sao-the-golden-star-5\" width=\"600\" height=\"340\" /></p>\r\n<h3 data-fontsize=\"14\" data-lineheight=\"21\"><strong>Gần khu đô thị Phú Mỹ Hưng</strong></h3>\r\n<p>The Golden Star nằm liền kề khu đô thị Phú Mỹ Hưng với tổng cộng 478 căn hộ diện tích từ 50-83 m2, giá bán chỉ từ 1,4 tỉ đồng/căn, phù hợp với đông đảo khách hàng trẻ năng động và thành đạt. Đặc biệt, chủ đầu tư đang áp dụng chính sách thanh toán ưu đãi, chỉ cần thanh toán trước 30% (500 triệu đồng) là sẽ trở thành chủ nhân căn hộ đạt chuẩn 5 sao; phần còn lại trả góp mỗi tháng từ 8-15 triệu đồng trong 10 năm.</p>\r\n<p><img class=\"aligncenter size-full wp-image-2105\" src=\"http://www.hunglocphat.com/wp-content/uploads/2016/09/su-khac-biet-cua-can-ho-5-sao-the-golden-star-6.jpg\" alt=\"su-khac-biet-cua-can-ho-5-sao-the-golden-star-6\" width=\"600\" height=\"409\" /></p>\r\n<p>Hơn 300 khách hàng đã đến tham quan nhà mẫu The Golden Star trong ngày khai trương đón khách hôm 9-4 vừa qua. Đẳng cấp của dự án này đã khiến nhiều khách hàng hài lòng vào toàn bộ 150 căn hộ của đợt 2 đã được khách hàng đặt chỗ mua.</p>\r\n<h3 data-fontsize=\"14\" data-lineheight=\"21\"><cite>Thông tin chi tiết xin liên hệ:</cite></h3>\r\n<p><cite>Công ty cổ phần Đầu tư bất động sản Hưng Lộc Phát</cite></p>\r\n<p><cite>Hotline: 0916.968.968</cite></p>\r\n<p><cite>Website: www.localhost/an</cite></p>', 2, '2018-04-04 17:03:00', NULL, NULL);
INSERT INTO `tbl_products` VALUES (23, 2, '[Báo Sài Gòn Đầu Tư] Chọn mặt, gửi tiền', '[Báo Sài Gòn Đầu Tư] Chọn mặt, gửi tiền', '1506153413_146088_3-0-phoi-canh-sanh-can-ho-3.jpg', '[Báo Sài Gòn Đầu Tư] Chọn mặt, gửi tiền', '<h3 data-fontsize=\"14\" data-lineheight=\"21\"><strong><em>(ĐTTCO) – Thị trường và chính sách ngày càng bắt buộc doanh nghiệp đầu tư – kinh doanh <a title=\"bất động sản\" href=\"http://hunglocphat.com/bat-dong-san\">bất động sản</a> phải có thực lực cũng như chuyên nghiệp hơn. Dự thảo Thông tư 36 siết lại tín dụng <a title=\"Bất Động Sản\" href=\"http://hunglocphat.com/bat-dong-san.html\">Bất Động Sản</a> là một tín hiệu thanh lọc những doanh nghiệp yếu kém trong thời gian tới.</em></strong></h3>\n<p><img class=\"aligncenter size-full wp-image-2015\" src=\"http://www.hunglocphat.com/wp-content/uploads/2016/09/phoi-canh-buoi-toi-the-golden-star.jpg\" alt=\"phoi-canh-buoi-toi-the-golden-star\" width=\"6300\" height=\"4136\" /></p>\n<p>     Thực ra dự thảo sửa đổi Thông tư 36 của NHNN được các doanh nghiệp đón nhận với nhiều trạng thái khác nhau. Hiệp hội BĐS TPHCM đã có ngay văn bản gửi NHNN và các cơ quan chức năng đề nghị không nên sửa đổi trong thời điểm hiện nay sẽ gây khó cho thị trường khi vừa mới hồi phục. Bởi hiện nay các doanh nghiệp BĐS chủ yếu phụ thuộc vào 2 nguồn vốn chính: vốn vay từ ngân hàng và vốn huy động từ khách hàng, mà nguồn vốn khách hàng phần lớn lại có nguồn gốc từ ngân hàng.</p>\n<p>    Ông Ngô Quang Phúc, Phó Tổng giám đốc Him Lam Land, cho rằng khi thị trường mới ấm lên Nhà nước không nên chặn lại mà có giải pháp để thị trường phát triển. Song phải thừa nhận việc siết tín dụng cũng có mặt tích cực lớn là thanh lọc chủ đầu tư, loại bỏ được những dự án không hiệu quả, hạn chế đầu cơ. Thực tế thời gian qua nhiều doanh nghiệp vẫn chứng tỏ không quá lệ thuộc vào nguồn vốn vay từ ngân hàng. Mới đây, CTCP Địa ốc Phú Long công bố một dự án mới, theo đó khách hàng chỉ cần đóng 30% giá trị căn hộ cho đến khi nhận nhà mới tiếp tục đóng phần còn lại. Trước đó, CTCP Địa ốc Him Lam đầu tư dự án Him lam Chợ Lớn (quận 6), dự án xây xong mới đưa ra thị trường.</p>\n<p>    Tiếp nối thành công của 2 dự án Carillon 1 và Carillon 2, Sacomreal hiện đang tung ra thị trường dự án căn hộ Carillon 3 (quận Tân Bình) với giá chỉ 1,68 tỷ đồng/căn. Carillon 3 bao gồm 1 block với 90 căn hộ thương mại, 6 shophouse và 1 penthouse. Nhằm hỗ trợ cho khách hàng mua nhà, ông Nguyễn Sinh Mỹ, Phó Tổng giám đốc Sacomreal, cho biết: “Chủ đầu tư sẽ áp dụng phương thức thanh toán linh hoạt, người mua nhà được hỗ trợ vay vốn ngân hàng tối đa 75% giá trị căn hộ. Để nâng cao chất lượng cuộc sống cho cư dân, Carillon 3 còn tích hợp nhiều tiện ích của một chung cư cao cấp, hiện đại, hệ thống an ninh tòa nhà 24/24″.</p>\n<p>    Ông Nguyễn Dư Lực, Chủ tịch HĐQT Công ty TNHH XD &amp; SX <a title=\"Hưng Lộc Phát\" href=\"http://www.hunglocphat.vn/\">Hưng Lộc Phát</a>, cho biết dự án Hưng Lộc Phát 1 triển khai trong bối cảnh thị trường khó khăn nhưng doanh nghiệp vẫn xây dựng hoàn thành và giao nhà đúng tiến độ cho khách hàng. Dự án Hưng Phát 2 chủ đầu tư chỉ thu 20% số tiền căn hộ của khách hàng đến khi nhận nhà. Chính sự uy tín này mà Hưng Lộc Phát chuẩn bị công bố dự án khu căn hộ cao cấp The Golden Star (Hưng Phát 3 số 72 Nguyễn Thị Thập, quận 7, TPHCM – ảnh) thuộc quy hoạch khu dân cư cao cấp Nam Sài Gòn với sự tham dự của hơn 900 khách hàng. Trong đợt công bố này, Hưng Lộc Phát đưa ra mức giá rất hấp dẫn chỉ từ 25,3 triệu đồng/m2 (rẻ hơn từ 2-3 triệu đồng so với các dự án cùng khu vực).The Golden Star là dự án khu căn hộ cao cấp có tổng diện tích 9.229m2, được xây dựng với quy mô 26 tầng, 478 căn hộ với tổng mức đầu tư khoảng 932 tỷ đồng. Trong đó các căn hộ có diện tích 50-83m2 từ 2 đến 3 phòng ngủ. Là dự án căn hộ thuộc dòng sản phẩm cao cấp nên chủ đầu tư rất chịu khó đầu tư cho The Golden Star: lát gạch cẩm thạch, ván lót sàn nhập khẩu từ Đức, thiết bị vệ sinh ToTo, thiết bị bếp Malloca. Để đảm bảo an toàn tuyệt đối cho cư dân, dự án được trang bị hệ thống bảo vệ an ninh 3 lớp, quản lý bằng video call và thẻ từ.</p>\n<p>    “Với tôi đi chậm mà chắc. Không đầu tư dàn trải. Làm dự án nào tập trung mọi khả năng về tài chính, con người để chăm chút cho dự án đó nhằm gây dựng niềm tin của khách hàng đối với sản phẩm” – ông Lực nói. Tại dự án The Golden Star, công ty phải tuyển chọn thương hiệu tư vấn hàng đầu Đảo quốc Singapore là Ong &amp; Ong (đơn vị tư vấn thiết kế cho dự án Vinhomes Tân Cảng). Ngay từ đầu, mọi công đoạn từ lên ý tưởng cho đến thiết kế chi tiết, hoàn thiện đều do Ong &amp; Ong đảm nhiệm nhằm đảm bảo sự hoàn hảo ở mức cao nhất. “Đây là căn hộ thuộc dòng sản phẩm cao cấp của chủ đầu tư Hưng Lộc Phát” – ông Lực chia sẻ.</p>\n<p><strong>Đỗ Bình Minh</strong></p>\n<p><strong>Nguồn: Saigondautu.com.vn</strong></p>', 2, '2017-09-23 14:56:53', NULL, NULL);
INSERT INTO `tbl_products` VALUES (24, 2, '[batdongsan.com.vn] Giảm gánh nặng tài chính cho người mua nhà', '[batdongsan.com.vn] Giảm gánh nặng tài chính cho người mua nhà', '1507882039_84439_20171012085110-af16.jpg', 'Hiện nay, nhiều chủ đầu tư (CĐT) hoàn thành dự án một cách “thần tốc”, vượt tiến độ bàn giao nhà theo cam kết. Thế nhưng, bên cạnh niềm vui được nhận nhà sớm thì người mua lại canh cánh nỗi lo tài chính vì phải đóng tiền theo tiến độ.', '<div class=\"detailsView-contents-style\">\n<h2 id=\"ctl28_ctl00_divSummary\" class=\"summary\">Hiện nay, nhiều chủ đầu tư (CĐT) hoàn thành dự án một cách “thần tốc”, vượt tiến độ bàn giao nhà theo cam kết. Thế nhưng, bên cạnh niềm vui được nhận nhà sớm thì người mua lại canh cánh nỗi lo tài chính vì phải đóng tiền theo tiến độ.</h2>\n</div>\n<div id=\"divContents\" class=\"detailsView-contents-style detail-article-content\">\n<h2>Xoay tiền không kịp</h2>\n<p>Thị trường bất động sản (BĐS) hiện nay không hiếm các dự án có tốc độ xây dựng khá nhanh. Trên địa bàn Tp.HCM phải kể đến một số dự án có tiến độ thi công “thần tốc” như River Gate, Icon 56 của Novaland; dự án Him Lam An Phú, Him Lam Phú Đông của Công ty CP Địa ốc Him Lam hay một số dự án của Hưng Thịnh như: Lavita Garden, Sky Center, Richmond City; Viva Riverside (Vietcomreal), Hà Đô Centrosa Garden (Tập đoàn Hà Đô), The Golden Star (Công ty Hưng Lộc Phát)....</p>\n<p>Hầu hết các dự án xây nhanh đều huy động vốn của khách mua theo tiến độ xây dựng. Cụ thể, lên 1-2 sàn, CĐT lại huy động vốn của người mua một lần để bù đắp vào chi phí thi công. Bên cạnh niềm vui được sở hữu căn hộ sớm thì không ít khách mua điêu đứng vì phải xoay tiền liên tục trong khoảng thời gian ngắn để đóng theo tiến độ. Chị Lương Thị Liễu, một khách hàng mua căn hộ tại Q.Thủ Đức chia sẻ: “Cứ khoảng 2 – 3 tháng, tôi phải đóng vài trăm triệu đồng cho CĐT. Vì dự án xây quá nhanh nên lúc nào tôi cũng sống trong tâm trạng lo lắng, bất an vì không biết làm sao để xoay đủ tiền”. Tương tự, anh Hà Trọng Phúc (ngụ Q.7) mua một căn hộ tại Q.Bình Thạnh vào giữa năm 2016 cũng “chóng mặt” vì chạy vạy vay người thân, ngân hàng, lãi ngoài… để đóng tiền theo tiến độ dự án của CĐT. “Giờ gia đình tôi chỉ mong dự án từ từ hãy bàn giao. Vì nếu giao nhà sớm, tôi không biết lo đâu gần 400 triệu đồng để đóng vào”, anh Phúc giãi bày.</p>\n<p>Trường hợp như anh Phúc, chị Liễu không phải hiếm trong bối cảnh thị trường BĐS hiện nay. Nếu trước đây, tiến độ dự án chậm là nỗi ám ảnh của người mua nhà thì hiện nay, tốc độ xây dựng quá nhanh lại khiến họ chật vật với bài toán tài chính nếu như không có quỹ dự phòng. Theo phân tích của các chuyên gia, bài toán huy động vốn theo tiến độ cho dự án hình thành trong tương lai vừa hợp lý mà vừa bất lợi. Hợp lý vì xây đến đâu đóng tiền đến đó sẽ có lợi cho cả CĐT và người mua, tạo niềm tin ở khách hàng. Thế nhưng, việc xoay chuyển dòng vốn của khách mua sẽ gặp khó khăn nếu vốn sẵn có không mạnh. Đặc biệt là những nhà đầu tư chưa ra được hàng sẽ bất lợi khi dự án xây quá nhanh, đóng tiền liên tục.</p>\n<table align=\"center\">\n<tbody>\n<tr>\n<td><img title=\"Giảm gánh nặng tài chính cho người mua nhà \" src=\"https://file4.batdongsan.com.vn/2017/10/12/b9sp0zUm/20171012085110-af16.jpg\" alt=\"Dự án The Golden Star \" /></td>\n</tr>\n<tr>\n<td><em>Dự án The Golden Star hiện đã xây dựng đến tầng 24,<br />dự kiến bàn giao nhà vào tháng 6/2018</em></td>\n</tr>\n</tbody>\n</table>\n<h2> </h2>\n<h2>Giảm áp lực tài chính cho khách mua</h2>\n<p>Khách hàng gặp khó khăn khi phải đóng tiền theo tiến độ dự án, nhất là các dự án xây “thần tốc” là thực tế của thị trường BĐS hiện nay. Do đó, việc một số CĐT giãn tiến độ thanh toán, linh động hỗ trợ người mua bằng hình thức trả chậm bất chấp tiến độ dự án đang được nhiều khách hàng hoan nghênh. Trên thị trường Tp.HCM, một số CĐT như Hưng Lộc Phát, Him Lam, Phú Long… đang thực hiện tốt chính sách chia sẻ khó khăn với khách hàng bằng việc thanh toán chậm, hỗ trợ lãi suất ưu đãi. Người mua nhà vì thế sẽ cảm thấy không bị áp đặt, đủ thời gian để xoay dòng tiền mặc dù dự án vẫn thi công đúng tiến độ đề ra.</p>\n<p>Với dự án <a href=\"http://localhost/an/\" rel=\"Nofollow\" target=\"_blank\">The Golden Star</a> tọa lạc tại ngay mặt tiền đường Nguyễn Thị Thập, Q.7 của CĐT Hưng Lộc Phát, khách hàng sẽ được nhận nhà sớm vì tốc độ xây dựng nhanh. Hiện tại, dự án đã thi công đến tầng 24, dự kiến bàn giao nhà cho khách mua vào quý II/2018. Điều đáng nói, mặc dù tốc độ xây nhanh nhưng CĐT lại linh hoạt hỗ trợ, nhằm giảm áp lực tối đa về tài chính cho khách hàng.</p>\n<p>Cụ thể, trong đợt bán hàng 100 cuối cùng của dự án The Golden Star, CĐT đã áp dụng đến 4 phương thức thanh toán linh hoạt. Khách hàng có thể chọn thanh toán theo tiến độ thời gian giãn cách, hay áp dụng lịch thanh toán 2%/tháng. Ngoài ra, dự án còn được ngân hàng Vietcombank bảo lãnh và hỗ trợ khách vay với lãi suất hấp dẫn từ 7,7%/năm, trả góp trong vòng 10 – 15 năm. Bên cạnh đó, những khách hàng mua trong đợt bán hàng này còn được CĐT tặng quà tân gia 2 chỉ vàng SJC và bốc thăm trúng Mazda.</p>\n<table align=\"center\">\n<tbody>\n<tr>\n<td><img title=\"Giảm gánh nặng tài chính cho người mua nhà 1\" src=\"https://file4.batdongsan.com.vn/2017/10/12/b9sp0zUm/20171012085209-b911.jpg\" alt=\"Căn hộ The Golden Star\" /></td>\n</tr>\n<tr>\n<td><em>Khách mua sẽ thanh toán linh hoạt 2%/tháng đến lúc nhận nhà,<br />ngân hàng hỗ trợ lãi suất ưu đãi</em></td>\n</tr>\n</tbody>\n</table>\n<p>Chia sẻ về chính sách hỗ trợ thanh toán linh hoạt cho khách mua, ông Nguyễn Trung Tuấn, Giám đốc kinh doanh Sàn BĐS Hưng Lộc Phát cho biết: “Mọi chính sách bán hàng của CĐT đều hướng đến việc phục vụ tốt nhất cho khách mua. Do đó, việc định giá bán hợp lý, hỗ trợ thanh toán linh hoạt mà CĐT đưa ra nhằm giúp khách hàng giảm tối đa áp lực tài chính và có cơ hội sở hữu căn hộ”. Theo ông Tuấn, đối với khách hàng mua ở thì việc hỗ trợ tài chính bằng hình thức trả chậm sẽ giúp họ dễ dàng có được chốn an cư; còn đối với NĐT, thanh toán linh hoạt sẽ giúp họ chủ động xoay chuyển dòng vốn. Từ đó tạo tính thanh khoản lâu dài cũng như tỉ suất lợi nhuận ổn định khi đầu tư căn hộ.</p>\n<p>Điểm đáng chú ý của dự án The Golden Star là mặc dù sắp hoàn thiện, tiến độ xây dựng đảm bảo nhưng chính sách huy động vốn lại cực kỳ ưu đãi cho khách mua. CĐT giãn tiến độ, chia nhỏ các đợt thanh toán với giá trị thấp để khách hàng sắp xếp nguồn tài chính.</p>\n<p>Rõ ràng, trong bối cảnh huy động vốn khi chưa đủ điều kiện; nhiều dự án đắp chiếu vì CĐT yếu năng lực tài chính đang là bài toán nhức nhối của thị trường BĐS thì những dự án như The Golden Star thực sự không nhiều. Điều này cũng chứng tỏ, CĐT phải có tiềm lực tài chính đủ mạnh cộng hưởng năng lực, sự chuyên nghiệpvà kinh nghiệm quản lý BĐS lâu năm mới có thể làm được.</p>\n</div>\n<div id=\"ctl28_ctl00_divSourceNews\" class=\"detailsView-contents-style soucenews\"><em>(Theo batdongsan.com.vn)</em> </div>\n<div class=\"detailsView-contents-style soucenews\">https://batdongsan.com.vn/tin-thi-truong/giam-ganh-nang-tai-chinh-cho-nguoi-mua-nha-ar88912</div>', 2, '2017-10-13 15:07:19', NULL, NULL);
INSERT INTO `tbl_products` VALUES (25, 1, 'Tiện ích', 'Tiện ích ', NULL, '', '<p>Mục tiêu hướng tới cuộc sống đầy đủ tiện ích cho tất cả cư dân, chủ đầu tư Hưng Lộc Phát đã thiết kế những khu tiện ích chuyên biệt phục vụ mọi mặt nhu cầu cuộc sống tại can ho Green Star. Không chỉ hướng tới không gian sống xanh – trong lành, Hưng Lộc Phát còn muốn phát triển một dự án là khu phức hợp khép kín có cả trung tâm ý tế chăm sóc sức lẫn trường học cho dân cư của The Green Star.</p>\r\n<p style=\"text-align: center;\"><img class=\"img-responsive\" title=\"3.Phong khach-V3(A1).jpg\" src=\"http://greenstarskygardens.com/v2.jpg\" alt=\"\" width=\"1500\" /></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: large;\"> </span></p>\r\n<div id=\"attachment_140\" class=\"wp-caption aligncenter\" style=\"text-align: center;\"><strong><span style=\"font-family: \'times new roman\', times; font-size: large;\"> Hồi bơi tại Green Star</span></strong></div>\r\n<div class=\"wp-caption aligncenter\" style=\"text-align: center;\"> </div>\r\n<div class=\"wp-caption aligncenter\" style=\"text-align: center;\"><strong><span style=\"font-family: \'times new roman\', times; font-size: large;\"><img class=\"img-responsive\" title=\"3.Phong khach-V3(A1).jpg\" src=\"http://greenstarskygardens.com/v1.jpg\" alt=\"\" width=\"2500\" height=\"1348\" /></span></strong></div>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: large;\"><img class=\"img-responsive\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://green-stars.vn/wp-content/uploads/2018/03/green-star-tien-ich-can-ho.jpg\" alt=\"\" width=\"845\" height=\"393\" /><img class=\"img-responsive\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://green-stars.vn/wp-content/uploads/2018/03/tien-ich-can-ho-Green-Star.jpg\" alt=\"\" width=\"845\" height=\"335\" /></span></p>\r\n<h3><span style=\"font-family: \'times new roman\', times; font-size: large;\">Căn hộ có các tiện ích nổi bật đạt chuẩn 5 sao:</span></h3>\r\n<div class=\"content content-2 fadeInLeftShort animated delay-0 col-fluid-0 go\">\r\n<div class=\"col-xs-6 col-sm-6 col-md-6 col-lg-6\">\r\n<ul class=\"doted list-tien-ich\">\r\n<li><span style=\"font-family: \'times new roman\', times; font-size: large;\">Khu vườn hẹn ước</span></li>\r\n<li><span style=\"font-family: \'times new roman\', times; font-size: large;\">Ốc đảo tình yêu</span></li>\r\n<li><span style=\"font-family: \'times new roman\', times; font-size: large;\">Vườn dưỡng sinh</span></li>\r\n<li><span style=\"font-family: \'times new roman\', times; font-size: large;\">Cầu mặt trời</span></li>\r\n<li><span style=\"font-family: \'times new roman\', times; font-size: large;\">Hồ hoa súng</span></li>\r\n<li><span style=\"font-family: \'times new roman\', times; font-size: large;\">Vườn hướng dương</span></li>\r\n<li><span style=\"font-family: \'times new roman\', times; font-size: large;\">Vườn tình yêu</span></li>\r\n<li><span style=\"font-family: \'times new roman\', times; font-size: large;\">Khuôn viên ven hồ</span></li>\r\n<li><span style=\"font-family: \'times new roman\', times; font-size: large;\">Lối vào quảng trừơng Diamond</span></li>\r\n<li><span style=\"font-family: \'times new roman\', times; font-size: large;\">Công viên nước cho trẻ con</span></li>\r\n<li><span style=\"font-family: \'times new roman\', times; font-size: large;\">...</span></li>\r\n</ul>\r\n</div>\r\n<div class=\"col-xs-6 col-sm-6 col-md-6 col-lg-6\">\r\n<ul class=\"doted list-tien-ich\">\r\n<li><span style=\"font-family: \'times new roman\', times; font-size: large;\">Công viên hoa ngũ sắc</span></li>\r\n<li><span style=\"font-family: \'times new roman\', times; font-size: large;\">Hồ bơi skyview</span></li>\r\n<li><span style=\"font-family: \'times new roman\', times; font-size: large;\">Bồn Jacuzzi</span></li>\r\n<li><span style=\"font-family: \'times new roman\', times; font-size: large;\">Phố đi bộ Vườn thượng uyển</span></li>\r\n<li><span style=\"font-family: \'times new roman\', times; font-size: large;\">Gym</span></li>\r\n<li><span style=\"font-family: \'times new roman\', times; font-size: large;\">Đài phun nước</span></li>\r\n<li><span style=\"font-family: \'times new roman\', times; font-size: large;\">Vườn ngắm sao</span></li>\r\n<li><span style=\"font-family: \'times new roman\', times; font-size: large;\">Hồ Luna</span></li>\r\n<li><span style=\"font-family: \'times new roman\', times; font-size: large;\">Vườn địa đàng</span></li>\r\n<li><span style=\"font-family: \'times new roman\', times; font-size: large;\">Khu sinh hoạt cộng đồng</span></li>\r\n</ul>\r\n</div>\r\n</div>\r\n<div class=\"vc_row wpb_row vc_row-fluid dt-default\">\r\n<div class=\"wpb_column vc_column_container vc_col-sm-12\">\r\n<div class=\"vc_column-inner \">\r\n<div class=\"wpb_wrapper\">\r\n<div class=\"wpb_text_column wpb_content_element \">\r\n<div class=\"wpb_wrapper\">\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: large;\"> </span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: large;\"><img class=\"img-responsive\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://green-stars.vn/wp-content/uploads/2018/03/tien-ich-green-star.jpg\" alt=\"\" width=\"845\" height=\"422\" /><img class=\"img-responsive\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://green-stars.vn/wp-content/uploads/2018/03/ho-canh-quan-biet-thu-green-star-quan-7.jpg\" alt=\"\" width=\"845\" height=\"225\" /></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: large;\"> </span></p>\r\n<p><span style=\"font-family: \'times new roman\', times; font-size: large;\">Đặc biệt, Hưng Phát Green Star nổi bật hơn với khu công viên cây xanh có diện tích hơn 7.000 m2 và hồ cảnh quan rộng gần 4.000 m2. Có thể nói Chủ đầu tư Hưng Lộc Phát kiến tạo một không gian sống xanh, cực thoáng mát với đầy đủ các tiện ích phục vụ mọi mặt cho cuộc sống của cư dân tại đây.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: large;\"><em>Khi thiết kế dự án chủ đầu tư HLP đặt biệt chú trọng về tiện ích cho cư dân. Tất cả tiện ích gì có thể để tạo cho cư dân có cuộc sống tiện lợi nhất thì chủ đầu tư đã không ngần ngại đầu tư cho cư dân vì vậy có thể nói đây là dự án với tiện ích nổi trội nhất cụ thể như sau:</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: large;\">Công viên quanh hồ được thiết kế bởi công ty chuyên thiết kế cảnh quan và đưa ra ý tưởng thiết kế theo kiểu Nhật với hình dáng gảy khúc lạ mắt.  Giữa hồ là đài phun nước ban đêm những đài phun nước nhảy múa theo tiếng nhạc và ánh sánh tạo tạo ra khung cảnh huyền ảo và thơ mộng như ở đảo Sentosa ở Singapore hoặc là ở khách sạn Venicent ở Las Vegas  cảm súc về đêm trong dự án Green Star như ở Đảo Sentosa của Sigapore cũng như ở Las Vegas hàng đêm cư dân và khách bên ngoài đển công viên hồ để thưởng ngoạn.</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: \'times new roman\', times; font-size: large;\">Những tiện ích khác như lối đi bộ được thiết kế uốn khúc lượn quanh bờ hồ cho cư dân tập thể dục và những dãi ghế đá để nghỉ ngơi – trò chuyện – thư giãn trong khung cảnh mát mẻ của hồ nước mênh mông và hàng cây bóng mát được tuyển chọn rất đẹp giống như đi trong công viên safari.</span></li>\r\n<li><span style=\"font-family: \'times new roman\', times; font-size: large;\">Khu BBQ được thiết kế 2 khu vực đó là ở khu Công viên hồ và trên tầng 5 tầng hồ bơi của khu căn hộ và Club house được bố trí gần nhau để cư dân tổ chức tiệc tùng vào những ngày lễ hội cùng với gia đình, người thân và bạn bè mà không cần phải đi đâu xa đồng thời trải nghiệm không khí mát mẻ ấm cúng tiện ích và có ý nghĩa mà hiếm nơi nào có được.</span></li>\r\n<li><span style=\"font-family: \'times new roman\', times; font-size: large;\">Khu vui chơi đành cho trẻ con tha hồ tung tăng đùa nghịch với cầu tuột – dây leo – xích đu vv…  ngoài ra còn có khu hồ bơi và sân chơi – sân banh cho trẻ con ở tầng 5.</span></li>\r\n<li><span style=\"font-family: \'times new roman\', times; font-size: large;\">Khu tập thể dục của người lớn với các dụng cụ tập thể dục đặt ngay tại công viên hồ.</span></li>\r\n<li><span style=\"font-family: \'times new roman\', times; font-size: large;\">Quán Càfe mini thuỷ tạ xây ra phía hồ nơi mà cư dân có thể thư giãn thả mình nhâm nhi với tách càfe và ăn nhẹ. Chủ đầu tư đã dành thật nhiều mặt bằng café – cửa hàng ăn uống khắp dự án từ hồ sinh thái đến sảnh đón tầng 1 và tầng 5 khu căn hộ điều này tạo thuận lợi vô cùng cho cư dân chỗ nào cư dân cũng có thể tiếp khách – thư giãn – nghĩ ngơi và thưởng thức nhu cầu ẩm thực.</span></li>\r\n<li><span style=\"font-family: \'times new roman\', times; font-size: large;\">Dãy phố thương mại dịch vụ dọc hai bên đường trục 20 mét tại đây cư dân có thể vừa ở vừa kinh doanh sinh lợi hàng tháng vừa tiện nghi với các của hàng tiện ích – cửa hàng thực phẩm tươi sống – cửa hàng gia dụng – nhà thuốc tây – các dịch vụ như giặt ủi – chăm sóc da – Spa – dịch vụ làm tóc – nail – dịch dụ tư vấn pháp lý vv… khi dự án đi vào hoạt động với hơn 6.000 cư dân của dự án cùng với 10.000 cư dân giáp dự án sẽ tạo thành khu phố đô thị thương mại – sầm uất – văn minh như là downtown (thị tứ)</span></li>\r\n<li><span style=\"font-family: \'times new roman\', times; font-size: large;\">Dãy nhà quanh hồ chính là những hàng quán tập trung về đêm nơi mà cư dân trong và ngoài dự án đến đây để thưởng ngoạn – ăn uống – ngắm cảnh về đêm.</span></li>\r\n<li><span style=\"font-family: \'times new roman\', times; font-size: large;\">Sảnh đón cao cấp và sang trọng như khách sạn 5 sao nhìn ra khu hồ cảnh quan được bố trí bàn tiếp khách cùng khu vực café kế bên rất tiện lợi cho việc tiếp khách.</span></li>\r\n<li><span style=\"font-family: \'times new roman\', times; font-size: large;\">Hồ bơi tràn Sky view dành cho người lớn rộng 640 m2 được lọc Ozone không gây ảnh hưởng tới da và tai – mũi họng; hồ Jacuzzi – phòng xông hơi – phòng thay đồ – Phòng Gym – Spa – nail bên cạnh đó là hồ bơi trẻ và khu vui chơi cho trẻ con – cửa hàng food mark với đầy đủ thực thẩm tươi sống đáp ứng nhu cầu hàng ngày của cư dân mà không cần đi đâu xa – nhà hàng và những cửa hàng ăn uống – câu lạc bộ – nhà trẻ – mẫu giáo – gym – vv…</span></li>\r\n<li><span style=\"font-family: \'times new roman\', times; font-size: large;\">Tại dự án cư dân có thể tổ chức ngày hội hoa xuân – chợ đêm – chợ ẩm thực – điểm vui chơi văn hoá dân gian. Cư dân có thể tổ chức buổi tiệc ngoài trời nay tại công viên – hay cub house</span></li>\r\n<li><span style=\"font-family: \'times new roman\', times; font-size: large;\">Khu vực hồ cảnh quan phía trước khu chung cư, tầng 1 và tầng 5 khu chung cư thật sự là nơi hội tụ – điểm văn hoá của cư dân dự án.Nơi mà cư dân có thể làm quen với nhau – chia sẽ cho nhau và tạo tình đoàn đết tương thân tương trợ.</span></li>\r\n</ol>\r\n<h2 style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: large;\">TIỆN ÍCH NGOẠI KHU NỔI BẬT</span></h2>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: large;\">Từ dự án cư dân sẽ được hưởng trọn tiện ích như:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: large;\">+Về thương mại:khu Cresen mall,siêu thị Coop Mart, khu Parkson Paragon, khu mua sắm Vivo City , chợ truyền thống Phước Long, chợ truyền thống Tân Mỹ, hệ thống ngân hàng đầy đủ…</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: large;\">+Về y tế: Bệnh viện Pháp Việt ( bệnh viện quốc tế), bệnh viện Tâm Đức ( chuẩn quốc tế), bệnh viện đa khoa quận 7…</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: large;\">+Về giáo dục: Trường Nam Sài gòn, trường Đinh Thiện Lý, trường cấp 2 Phạm Hữu Lầu, trường cấp 2 Hoàng Quốc Việt, trường cấp 3 Ngô Quyền, trường đại học Tôn Đức Thắng, trường đại học Marketing, đại học quốc tế RMIT, cao đẳng Quản trị doanh nghiệp, trường đại học Cảnh Sát …</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: large;\">+Về hoạt động giải trí và thể thao:Hồ Bán Nguyệt, cầu Ánh Sao, sân Golf Phú Mỹ Hưng, sân đá bóng, sân tenis, hồ bơi, công viên Nam Sài Gòn, rạp chiếu phim tại Cresen mall, Parkson, Vivo city, rap chiếu phim Lotte mart…</span></p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>', 2, '2018-04-03 11:37:43', NULL, NULL);
INSERT INTO `tbl_products` VALUES (26, 3, 'Giới Thiệu Dự Án', '', NULL, '', '<p>Xuất phát từ ý tưởng cùng với tâm huyết xây dựng lên một khu đô thị văn minh. Hiện đại dư án khu đô thị mới XUYÊN MỘC HOANGLONGRIVERSIDE  ra đời với sứ mệnh dem đến sự trải nghiệm từ một cuộc sống hạnh phúc trọn vẹn. Trong lành và gần gũi với thiên nhiên. Một nơi lí tưởng cho cư dân </p>\r\n<p>Khu đô thị Hoàng Long River Side tọa lạc tại vị trí phía tây của huyện Xuyên Mộc .Mặt tiền quốc lộ 55 Hoàng Long River Side nằm trên vùng đất có phong thủy vượng phát .Được bao quanh bởi sông nước .Hòa hợp với thiên nhiên, đem lại sự thịnh vượng cho cư dân .Khu đô thị hoàng long river side sở hựu vị trí đại cát đại lợi đáp ứng hoàn hảo cả 3 tiêu chia vàng khi chọn đất là :Nhất cận thị ,Nhị cận gian, tam cận lộ </p>', 1, '2018-05-25 03:45:37', NULL, NULL);

-- ----------------------------
-- Table structure for tbl_tintuc
-- ----------------------------
DROP TABLE IF EXISTS `tbl_tintuc`;
CREATE TABLE `tbl_tintuc`  (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `cat_id` int(11) NOT NULL,
  `name` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `tittle` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `image` varchar(500) CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL DEFAULT NULL,
  `description` varchar(20000) CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL DEFAULT NULL,
  `content` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL,
  `status` tinyint(1) NULL DEFAULT 1 COMMENT '0.hết , 1.còn',
  `create_date` datetime(0) NULL DEFAULT NULL,
  `vitri` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL,
  `nhamau` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL,
  `matbang` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL,
  `tienich` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL,
  `tukhoa` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL,
  `vt` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL,
  `gia` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL,
  `dt` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL,
  `hot` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL,
  `thutu` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL,
  `tintuc` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 21 CHARACTER SET = utf8 COLLATE = utf8_unicode_ci ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of tbl_tintuc
-- ----------------------------
